import logo from './logo.svg';
import './App.css';
import { render } from '@testing-library/react';

class  App extends React.Component {

  constructor(props) {
    super(props);

    this.state = {contacts: []};
  }




  componentDidMount() {
    fetch("https://cors-anywhere.herokuapp.com/https://api.yelp.com/v3/businesses/search?term=icecream&location=alpharetta&limit=5",{
    method:'GET',
    headers: {
      'Authorization':'Bearer Wzy8m1uH0-3PzPzJsuVjsT9zlyClZimWrInChwNzSDF8q2BCd-I_6vgb8KTLiv-6Yjs9qjG-ymqDmR-buHuCNiUZuJtQFUfyGOivlBFFIspD9ZV9a2txNvZLXCBVX3Yx',
  }

    })
      .then(response => response.json())
      .then((responseData) => {
        console.log("responseData "+JSON.stringify(responseData));
        this.setState({ contacts: responseData});
      })
      .catch(error => this.setState({ error })

      )}




render(){


  const persons = this.state.person.map((item, i) => (
    <div>
      <h1>{ item.businesses[i].id }</h1>
      <span>{ item.businesses[i].name  }, { item.businesses[i].location.display_address }</span>
    </div>
  ));


  return (
    <div className="App">
      <header className="App-header">

      <div id="layout-content" className="layout-content-wrapper">
        <div className="panel-list">{ persons }</div>
      </div>

      </header>
    </div>
  );

  }

}export default App;
